// App.js
import React, { useState, useEffect } from "react";
import Dashboard from './components/Dashboard';
import ProductForm from './components/ProductForm';
import StockTable from './components/StockTable';
import FontSizeController from './components/FontSizeController'; // Importa el componente
import { fetchProducts } from './api'; // Asegúrate de tener esta función para cargar productos desde el backend

function App() {
 /* const [products, setProducts] = useState([]);
  // Callback para actualizar la lista de productos
  const handleProductAdded = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]);
  };*/
  const [products, setProducts] = useState([]); // Estado centralizado de productos

  // Cargar productos al montar el componente
  useEffect(() => {
    const loadProducts = async () => {
      const items = await fetchProducts();
      setProducts(items);
    };
    loadProducts();
  }, []);

  // Callback para agregar un nuevo producto
  const handleProductAdded = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]);
  };

  // Callback para eliminar un producto (opcional si es necesario en StockTable)
  const handleProductDeleted = (deletedProductId) => {
    setProducts((prevProducts) => prevProducts.filter((product) => product._id !== deletedProductId));
  };


return (
  <div>
     <FontSizeController />
    <header>
      <h1 style={{ textAlign: "center", padding: "10px", backgroundColor: "#0056b3", color: "#fff" }}>
        Gestión de Inventario - Cafetería
      </h1>
    </header>
    <main>
      <Dashboard products={products} />
      <ProductForm onProductAdded={handleProductAdded} />
      <StockTable products={products} />
    </main>
  </div>
);
}

export default App;
